import React from 'react';
import { StyleSheet, View, Text, Image } from 'react-native';
import { useSelector} from "react-redux";
import { MediumAppText, BoldAppText } from '../styles/text'
import Divider from './Divider'
import { VictoryArea, VictoryChart, VictoryAxis } from 'victory-native';
import _ from 'lodash';
import { DateTime } from 'luxon'
import {Defs, LinearGradient, Stop } from "react-native-svg";

function MoodVisuals () {

  const historicalData = useSelector((state) => state.historicalData);
  const moodsData = _.map(historicalData, el => el.moods) 

  function create (array) {
    let obj = {}
    for (let i of array) {
      for (let j of i) {
        if (Object.keys(obj).includes(j)) {
          obj[j] = obj[j]+1
        } else {
          obj[j]= 1
        }
      }
    }
    return obj
  }

  function createMoods (obj) {
    let arr = [];
    for (let i in obj) {
      let newobj = {}
      newobj.mood = i
      newobj.value = obj[i]
      arr.push(newobj)
    }
    return arr;
  }

  function createValues (obj) {
    let arr = [];
    for (let i in obj) {
      let newobj = {}
      newobj.value = obj[i]
      arr.push(newobj)
    }
    return arr;
  }

  const moodsObject = create(moodsData)
  const moods = createMoods(moodsObject)
  const values = createValues(moodsObject)

  console.log(moodsObject)
  console.log(moods)
  console.log(values)

  return (
    <View style={styles.container}>
      <BoldAppText>Your moods for the past [week]</BoldAppText>
      <VictoryChart width={350} >
          <Defs>
            <LinearGradient id="gradient1"
              x1="0%" 
              y1="0%" 
              x2="25%"
              x3="100%" 
              y2="100%"
            >
              <Stop offset="0%" stopColor='#FF55B8'/>
              <Stop offset="100%" stopColor='#FF8787'/>
            </LinearGradient>
          </Defs>
        <VictoryArea 
        data={{moods}} 
        x={"moods"} 
        y={"values"}
        style={{ 
          data: { fill: 'url(#gradient1)' },
          parent: {}
          }}
        interpolation="natural"
        />
        <VictoryAxis
          tickCount={3}
        />
        <VictoryAxis dependentAxis
          style={{
            axis: {stroke: '#DFE2E2', opacity: 0.5},
            tickLabels: {fontsize:5, color: 'blue'},
          }}
          tickCount={2}
        />
      </VictoryChart>
      <Divider/>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
  },
});



export default MoodVisuals;
