import React, { useEffect } from 'react';
import { StyleSheet, View, Text } from 'react-native';
import HomeWelcome from '../components/HomeWelcome'
import Feeling from '../components/Feeling'
import Moods from '../components/Moods'
import Meetings from '../components/Meetings'
import SuggestionsList from '../components/SuggestionsList'
import InspirationalQuote from '../components/InspirationalQuote'
import ApiService from '../ApiService'
import { AppLoading } from 'expo';
import {useDispatch, useSelector } from "react-redux";

function HomeScreen () {

  // TODO:  use react query
  const dispatch = useDispatch();
  useEffect(() => {
    ApiService.getQuote()
    .then(quote => 
      dispatch({
        type: "UPDATE_QUOTE",
        payload: quote
      })
    )
  },[])

  const quoteItem = useSelector((state) => state.dailyQuote.todaysQuote);

  return (
    !quoteItem ?
    <AppLoading/>
    :
    <View style={styles.container}>
      <HomeWelcome style={{flex:1}}/>
      <InspirationalQuote style={{flex:0.25}} quote={quoteItem[0].q} author={quoteItem[0].a}/>
      <Feeling style={{flex:1}}/>
      <Moods style={{flex:2}}/>
      <Meetings style={{flex:0.5}}/>
      <SuggestionsList style={{flex:1}}/>
    </View>
  )
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    textAlign: 'center',
    backgroundColor: '#fff',
  },

});



export default HomeScreen;
