import React from 'react';
import { StyleSheet, View, Text, Image } from 'react-native';
import { useSelector} from "react-redux";
import { MediumAppText, BoldAppText } from '../../styles/text'
import Divider from '../Divider'
import { VictoryArea, VictoryGroup, VictoryChart, VictoryAxis } from './Victory';
import _ from 'lodash';
import { DateTime } from 'luxon'
import {Defs, LinearGradient, Stop, Svg } from "react-native-svg";
import colors from '../../styles/colors'



function FeelingGraph () {

  const chartTimePeriod = useSelector((state) => state.helper.chartTimePeriod)
  const historicalData = useSelector((state) => state.historicalData);
  const feelingData = _.map(historicalData, el => el.feeling) 
  const dateData = _.map(historicalData, el => el.date)
  const formattedDateData = dateData.map(date => DateTime.fromMillis(date).toFormat('LLL dd'))

  function create(array1, array2) {
    let list = [];
    let count = 0;
    for (let i of array1) {
      let obj = {}
      obj.date = i
      obj.feeling = array2[count]
      list.push(obj)
      count++
    }
    return list
  }

  const combinedList = create(formattedDateData, feelingData)
  console.log(combinedList)
  return (
    <View style={styles.container}>
      <BoldAppText style={{marginBottom:-20, fontSize:14}}>My mood tracker</BoldAppText>
      <VictoryChart width={370} height={225} style= {{
        parent: {
          overflow: 'visible',
        }
      }}>
        <VictoryArea 
        data={combinedList} 
        x="date" 
        y="feeling"
        style={{ 
          data: { fill: 'url(#gradient1)' },
          parent: {}
          }}
        interpolation="natural"
        />
        <VictoryAxis
          tickCount={4}
          tickFormat={(x)=> x}
        />

          <Defs>
            <LinearGradient id="gradient1"
              x1="10%" 
              y1="25%" 
              x2="50%"
              y2="0%"
              x3="100%" 
              y3="100%"
            >
              <Stop offset="0%" stopColor= {colors.cosmicLatte}/>
              <Stop offset="100%" stopColor={colors.green}/>
            </LinearGradient>
          </Defs>


      </VictoryChart>
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'center',
    justifyContent: 'center',
  },
});


export default FeelingGraph;
